import cv2
import numpy as np

def apply(input):
    image_data_base64 = ""
    image_data = base64.b64decode(image_data_base64)
    image = cv2.imdecode(np.fromstring(image_data, dtype=np.uint8), cv2.IMREAD_GRAYSCALE)
    return "Dimensions " + str(image.shape)
